mapper.select:
select 
 asid, assess.id, caseid, plate_Number, sum, photo, apply_User, apply_User_Phone,assessor.asname assessor, 
  DATE_FORMAT(assess_Time,'%Y年%m月%d日')assess_Time,case_Describe
  from assess JOIN assessor ON (assessor.asorid=assess.assessor)
  where 1=1
  
     and asid = #{asid,jdbcType=VARCHAR}
    
    
     and caseid = #{caseid,jdbcType=VARCHAR}
    
    
     and assessor = #{assessor,jdbcType=VARCHAR}
    
     
     and plate_Number = #{plateNumber,jdbcType=VARCHAR}
    
    
     and apply_User = #{applyUser,jdbcType=VARCHAR}
